
let sidebar=document.querySelector(".sidebar"),closeBtn=document.querySelector("#btn"),searchBtn=document.querySelector(".bx-search");function menuBtnChange(){sidebar.classList.contains("open")?closeBtn.classList.replace("bx-menu","bx-menu-alt-right"):closeBtn.classList.replace("bx-menu-alt-right","bx-menu")}document.addEventListener("DOMContentLoaded",()=>{closeBtn.addEventListener("click",()=>{sidebar.classList.toggle("open"),menuBtnChange()}),searchBtn.addEventListener("click",()=>{sidebar.classList.toggle("open"),menuBtnChange()})});

